        console.log(x)
        setDrafts(x)
        console.log(drafts)
        
    // }
   const elements = drafts.map(post => {<Post pb={post.publicationDate} pk={post.pk} title={post.title} authorpk={post.author} author={getAuthor(post.author)} text={post.text} />})


    return(
        <div>
            {elements}
        </div>
    )
}
