import { useState, useEffect } from "react"
import Post from "./post"
import axios from "axios"
export default function Saved() {
    const [posts, setPosts] = useState([])
    const [user, setUser] = useState({})
    var userid = localStorage.getItem("id")
    // function getUser() {
    //     axios.get(`/api/users/${userid}`).then(response => {
    //         setUser(response.data)
    //         getPosts()
    //     }
    //     )
    // }
    function getPosts() {
        axios.get(`/api/posts/${userid}/save/`).then(response => setPosts([response.data]))
    }
    useEffect(getPosts,[])


    var stat
    if(posts == [] || posts == false){
        stat = false
    }
    else{
        stat= true
    }
    const elements = posts.map(post => <Post likes={post.likes} profile={post.profile} publicationDate={post.publicationDate} pb={post.publicationShortDate} pk={post.pk} title={post.title} authorpk={post.author} text={post.text} />)

    return (

        <main>
            {stat ? elements : <p>you have no saved articles</p>}
        </main>
    )


}
