import Post from "./post"
import axios from "axios"
export default function Drafts(){
    const [posts, setPosts] = useState([])
    const [drafts, setDrafts] = useState([]) 
    const [author,setAuthor] = useState("") 


    function getPosts(){
        axios({
            method: "GET",
            url: "/api/posts/"
        }).then((response) => {
            const newposts = response.data
            setPosts(newposts)
            console.log(newposts)
        })
    }
    function getAuthor(pk) {
        axios({
            method: "GET",
            url: "/api/users/".concat(pk)
        }).then((response) => {
            setAuthor(response.data.username)
        })
        return author
    }
    useEffect(getPosts,[])

    posts.map(post =>{ if (post.publicationDate == null){
        setDrafts(prevD => [...prevD, post])
    }})
   const elements = drafts.map(post => {<Post pb={post.publicationDate} pk={post.pk} title={post.title} authorpk={post.author} author={getAuthor(post.author)} text={post.text} />})


    return(
        <div>
            {elements}
        </div>
    )
}
