import { Link, useLocation,useNavigate } from "react-router-dom"
import axios from "axios"
export default function PostPage() {
    const navigate = useNavigate()
    const userid = localStorage.getItem("id")
    function deletePost(){
        axios.delete(`/api/posts/${loc.state.pk}`).then(response => console.log(response))
        .catch(error => console.log(error.response.data)).then(navigate("/"))
    }
    function publishPost(){
        axios.post(`/api/posts/${loc.state.pk}/publish/`, {pk:loc.state.pk}).then(navigate("/"))
        
    }
    const loc = useLocation()
    function updatePost(){
        navigate(`posts/${loc.state.pk}/update` , {state : loc.state})
    }
    var status = ""
    if (loc.state.authorpk == userid){
        status = true
    }
    else{
        status = false
    }
    console.log(loc.state)

    return (
        <div>
            <h2>{loc.state.author}</h2>
            <h1>{loc.state.title}</h1>
            <p>{loc.state.text}</p>
            {status ? <button onClick={updatePost}>update this post </button> : ""}
            {status ? <button onClick={deletePost}>delete this post </button> : ""}
            {loc.state.publicationDate ? "" :   <button onClick={publishPost}>publish this post</button>}
        </div>
    )
}
