import { useState, useEffect } from "react"
import Post from "./post"
import axios from "axios"
import { useNavigate } from "react-router-dom"
import User from "./user"
export default function Profile() {
    var edit = document.getElementsByClassName("edit")[0]
    var followingList = document.getElementsByClassName("followingList")[0]
    var body = document.getElementsByTagName("main")[0]
    const nav = useNavigate("")
    const user = { username: localStorage.getItem("username"), email: localStorage.getItem("email"), id: localStorage.getItem("id"), profile: localStorage.getItem("profile") }
    const [posts, setPosts] = useState([])
    const [profile, setProfile] = useState({})
    const [form, setForm] = useState({})
    const [followings, setFollowings] = useState([])
    const [elements, setElements] = useState([])

    function getPosts() {
        axios(
            {
                method: "get",
                url: `/api/profile/posts/${user.id}/`
            }
        ).then(response => setPosts(response.data))

        axios.get(`/api/profile/${user.profile}`).then(response => setProfile(response.data))
        axios.get(`/api/followings/${user.id}/`).then(response => setFollowings(response.data))
    }
    followings.forEach(user => {
        axios.get(`/api/profile/${user.profile}/`).then(response => Object.assign(user, { prof: response.data }))
    })
    console.log(followings)

    function handleClick() {
        if (edit.style.display === "none") {
            edit.style.display = "flex"
            edit.style.opacity = 1
            body.setAttribute("class", "blur")


        }
        else {
            edit.style.display = "none"
            body.setAttribute("class", null)


        }
    }
    const postlist = []
    const items = posts.map(post => <Post likes={post.likes} class={"profile_post"} profile={post.profile} pb={post.publicationShortDate} pk={post.pk} title={post.title} authorpk={post.author} author={user.username} text={post.text} />)
    for (let i = 0; i < items.length; i++) {
        if (i++ % 2 === 0) {
            postlist.push(<div className="profile_post_line">{items[i]} {items[i - 1]} </div>)
        }

    }
    useEffect(getPosts, [user.id, user.profile])
    function handleChange(event) {
        if (event.target.name === "img") {
            setForm(prevForm => { return { ...prevForm, profile_image: event.target.src } })
        }
        if (event.target.name === "bio") {
            setForm(prevForm => { return { ...prevForm, bio: event.target.value } })
        }
    }
    function updateProfile(e) {
        e.preventDefault()
        const form_data = new FormData()
        form_data.append("bio", form.bio)
        form_data.append("profile_image", form.profile_image)
        axios.post(`/api/profile/${user.profile}`, form, {
            headers: {
                "Content-Type": "multipart/form-data",
            }
        }).then(response => console.log(response.data))
        console.log(form.bio)

        edit.style.display = "none"
        body.setAttribute("class", null)

    }

    function followingDisplay() {
        if (followingList.style.display === "none") {
            followingList.style.display = "flex"
            body.setAttribute("class", "blur")

        }
        else {
            followingList.style.display = "none"
            body.setAttribute("class", null)


        }
        setElements(followings.map(user => <User username={user.username} profile_image={user.prof ? user.prof.profile_image : ""} bio={user.prof ? user.prof.bio : ""} />))
    }


    return (
        <div>
            <main>
                <div className="profile">
                    <div className="personal_info">
                        <div className="profile_image">
                            {profile.profile_image ? <img id="profile" src={profile.profile_image} alt="" /> : ""}
                        </div>
                        <div className="info">
                            <div className="profile_email_box">
                                <h1>{user.username}</h1>
                                <h2>{user.email}</h2>
                            </div>
                            <p>{profile.bio}</p>
                        </div>
                        <div className="profile_functions">
                            <button className="button" onClick={handleClick}>Edit your profile</button>
                            <button onClick={() => { nav('/saved/') }} className="button" >Saved articles</button>
                            <button onClick={followingDisplay} className="button">Following</button>
                            <button className="button" >Followers</button>
                        </div>
                    </div>
                    <div className="profile_posts">
                        {postlist}
                    </div>

                </div>
                {/* <div className="followingList" style={{display:"none"}}>
                {elements}
            </div> */}
                <form onSubmit={updateProfile} className="edit">
                    <input onClick={handleChange} accept="image/jpeg,image/png,image/gif" name="img" type={"file"} className="circle" />
                    <div className="input">
                        <label htmlFor="bio">Bio :</label>
                        <input onChange={handleChange} type={"text"} name="bio" />
                    </div>
                    <button type="submit" className="button">Submit</button>
                </form>
            </main>
            
            <div className="followingList" style={{ display: "none" }}>
                {elements}
            </div>
        </div>
    )
}
