import { Outlet, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios"
export default function Post(props) {
    const page = "/posts/".concat(props.pk)
    const profilePage = "/users/".concat(props.authorpk)
    const [author, setAuthor] = useState("")
    const [profile, setProfile] = useState("")

    function getAuthor(pk) {
        axios({
            method: "GET",
            url: "/api/users/".concat(pk)
        }).then((response) => {
            setAuthor(response.data)
        })

    }
    useEffect(() => getAuthor(props.authorpk), [props.authorpk])

    function getProfile() {
        axios.get(`/api/profile/${props.profile}`).then(response => setProfile(response.data))
    }
    useEffect(getProfile, [author])
    return (
        <div className="post_box">
            <div className="post_profile">
                <img id="profile" src={profile.profile_image} alt="none" />
            </div>
            <div className="post_info">
                <h2><Link className="post_link" to={profilePage} state={{ "pk": props.authorpk, "profile": Number(author.profile) }}  >{author.username}</Link></h2>

                <h3><Link className="post_link" to={page} state={{pk:props.pk , publicationDate: props.publicationDate, author: props.authorpk, user: author.username, title: props.title, text: props.text, profile: props.profile }}>{props.title}</Link></h3>
                <p>{props.pb}</p>
            </div>
            <Outlet />
        </div>

    )
}
