import { useState, useEffect } from "react"
import Post from "./post"
import axios from "axios"
export default function Saved() {
    const [posts, setPosts] = useState([])
    const [user, setUser] = useState({})
    var userid = localStorage.getItem("id")
    function getUser() {
        axios.get(`/api/users/${userid}`).then(response => {
            setUser(response.data)
            getPosts(response.data.postsSaved)
        }
        )
    }
    useEffect(getUser, [])
    // useEffect(getPosts, [user])
    function getPosts(x) {
        // for (var s  in user.postsSaved) {
        //     axios.get(`/api/posts/${user.postsSaved[s]}`).then(response => setPosts(prev => {
        //         { return [...prev, response.data] }

        //     }))

        // }
        for (var i = 0 ; i < x.length ; i++ ){
            axios.get(`/api/posts/${parseInt(x[i])}`).then(response => setPosts(prev => {
                { return [...prev, response.data] }

            }))
        }
    }
        console.log(posts)
        const elements = posts.map(post => <Post likes={post.likes} profile={post.profile} publicationDate={post.publicationDate} pb={post.publicationShortDate} pk={post.pk} title={post.title} authorpk={post.author} text={post.text} />)

        return (

            <main>
                {elements}
            </main>
        )


    }
