import { Outlet, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios"
export default function Post(props) {
    const page = "/posts/".concat(props.pk)
    const profilePage = "/users/".concat(props.authorpk)
    const [author, setAuthor] = useState("")
    function getAuthor(pk) {
        axios({
            method: "GET",
            url: "/api/users/".concat(pk)
        }).then((response) => {
            setAuthor(response.data)
        })
    
}
    useEffect(() =>getAuthor(props.authorpk), [props.authorpk])
    console.log(author)
    return (
        <div className="post_box">
            {/* <img src={profile.profile_img} alt="" /> */}
            <h2><Link className="post_link" to={profilePage} state={{ "pk": props.authorpk }}  >{author.username}</Link></h2>

            <h3><Link className="post_link" to={page} state={{ publicationDate: props.publicationDate, authorpk: props.authorpk, author: author.username, title: props.title, text: props.text , profile : author.profile }}>{props.title}</Link></h3>
            <p>{props.pb}</p>
            <Outlet />
        </div>

    )
}
