import { useLocation } from "react-router-dom"
import { useState, useEffect } from "react"
import Post from "./post"
import axios from "axios"
export default function Profile() {
    var edit = document.getElementsByClassName("edit")[0]
    var page = document.getElementsByClassName("profile")[0]
    var body = document.getElementsByTagName("main")[0]
    const location = useLocation()
    const user = { username: localStorage.getItem("username"), email: localStorage.getItem("email"), id: localStorage.getItem("id") }
    const [posts, setPosts] = useState([])
    const [profile, setProfile] = useState({})
    const [form, setForm] = useState({})

    function getPosts() {
        axios(
            {
                method: "get",
                url: "/api/posts/"
            }
        ).then(response => setPosts(response.data))

        axios.get(`/api/profile/${user.id}`).then(response => setProfile(response.data))
    }
    function handleClick() {
        if (edit.style.display == "none") {
            edit.style.display = "flex"
            body.style.opacity = "0.8"
            edit.style.opacity = "2"
            document.body.style.backgroundColor = "rgba(0,0,0,0.5)"

        }
        else {
            edit.style.display = "none"
            body.style.opacity = "1"
            document.body.style.backgroundColor = "rgba(0,0,0,0)"


        }
    }
    function updateProfile() {
        axios.post(`/api/profile/${user.id}`, form,{
            headers: {
            "Content-Type": "multipart/form-data",
        }})
        console.log(form)

        edit.style.display = "none"
        body.style.opacity = "1"
        document.body.style.backgroundColor = "rgba(0,0,0,0)"
    }
    const x = posts.filter(post => post.author == user.id)
    const elements = x.map(post => <Post pb={post.publicationShortDate} pk={post.pk} title={post.title} authorpk={post.author} author={user.username} text={post.text} />)
    useEffect(getPosts, [user.id])
    function handleChange(event) {
        if (event.target.name === "img") {
            setForm(prevForm => { return { ...prevForm, profile_image: event.target.files[0] } })
        }
        if (event.target.name === "bio") {
            setForm(prevForm => { return { ...prevForm, bio: event.target.value } })
        }
    }

    return (
        <main>
            <div className="profile">
                <div className="personal_info">
                    <div class="profile_image">
                        {profile.profile_image ? <img src={profile.profile_image} alt="" /> : ""}
                    </div>
                    <div className="info">
                        <h1>{user.username}</h1>
                        <h2>{user.email}</h2>
                        <h3>{profile.bio}</h3>
                    </div>
                    <button className="profilebutton" onClick={handleClick}>Edit your profile</button>
                </div>
                <div className="content">
                    {elements}
                </div>

            </div>
            <div className="edit">
                <input onClick={handleChange} accept="image/jpeg,image/png,image/gif" name="img" type={"file"} className="circle" />
                <div className="input">
                    <label htmlFor="bio">Bio :</label>
                    <input onChange={handleChange} type={"text"} name="bio" />
                </div>
                <button onClick={updateProfile} className="profilebutton">Submit</button>
            </div>
        </main>
    )
}
