import { useLocation } from "react-router-dom"
import { useState, useEffect } from "react"
import Post from "./post"
import axios from "axios"
export default function UserProfile(){
    const location = useLocation()
    const [user,setUser] = useState({})
    const [posts,setPosts]= useState([])
    function getUser(){
        axios({
            method : "GET",
            url : '/api/users/'.concat(location.state.pk)
        }).then(response => setUser(response.data))
    }

    function getPosts(){
        axios(
            {
                method : "get",
                url : "/api/posts/"
            }
        ).then(response => setPosts(response.data))
    }
    const elements = posts.map(post => <Post pb={post.publicationDate} pk={post.pk} title={post.title} authorpk={post.author} author={user.username} text={post.text} />)

    useEffect(getUser, [location.state.pk])
    useEffect(getPosts, [location.state.pk])

    return(
        <div>
            <h1>{user.username}</h1>
            <h2>{user.email}</h2>
            {elements}
        </div>
    )
}
