import { Outlet, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios"
export default function Post(props) {
    const page = "/posts/".concat(props.pk)
    const profilePage = "/users/".concat(props.authorpk)
    const [author, setAuthor] = useState("")
    const [profile,setProfile] = useState("")
    function getAuthor(pk) {
        axios({
            method: "GET",
            url: "/api/users/".concat(pk)
        }).then((response) => {
            setAuthor(response.data)
        })
        return author.username
    }
    const authorname = getAuthor(props.authorpk)

    // function getProfile() {
    //     axios.get(`api/profile/${author.profile}`).then(response => setProfile(response.data) )
    //     return profile
    // }

    // const profile1 = getProfile()

    return (
        <div className="post_box">
            <img src={profile.profile_img} alt="" />
            <h2><Link className="post_link" to={profilePage} state={{ "pk": props.authorpk }}  >{authorname}</Link></h2>

            <h3><Link className="post_link" to={page} state={{ publicationDate: props.publicationDate, authorpk: props.authorpk, author: authorname, title: props.title, text: props.text }}>{props.title}</Link></h3>
            <p>{props.pb}</p>
            <Outlet />
        </div>

    )
}
