import { Outlet, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios"
export default function Post(props) {
    const page = "/posts/".concat(props.pk)
    const profilePage = "/users/".concat(props.authorpk)
    const [author, setAuthor] = useState("")
    const [profile, setProfile] = useState("")

    function getAuthor(pk) {
        axios({
            method: "GET",
            url: "/api/users/".concat(pk)
        }).then((response) => {
            setAuthor(response.data)
        })

    }
    useEffect(() => getAuthor(props.authorpk), [props.authorpk])

    function getProfile(pk){
        axios.get(`/api/profile/${pk}`).then(response => setProfile(response.data))
    }

    if (author == true ){
        useEffect(() => getProfile(Number(author.profile)), [author])
    }


    console.log(author)
    return (
        <div className="post_box">
            <img src={profile.profile_img} alt="none" />
            <h2><Link className="post_link" to={profilePage} state={{ "pk": props.authorpk, "profile": Number(author.profile) }}  >{author.username}</Link></h2>

            <h3><Link className="post_link" to={page} state={{ publicationDate: props.publicationDate, authorpk: props.authorpk, author: author.username, title: props.title, text: props.text, profile: Number(author.profile) }}>{props.title}</Link></h3>
            <p>{props.pb}</p>
            <Outlet />
        </div>

    )
}
