import { useEffect, useState } from "react"
import Post from "./post"
import axios from "axios"
export default function Drafts() {
    const [posts, setPosts] = useState([])

    const user = localStorage.getItem("username")
    const userid = localStorage.getItem("id")
    function getPosts() {
        axios({
            method: "GET",
            url: "/api/drafts/"
        }).then((response) => {
            const newposts = response.data
            setPosts(newposts)
            console.log(newposts)
        })
    }
    useEffect(getPosts, [])

    const x = posts.filter(post => post.author == userid)
    const elements = x.map(post => <Post pb={post.publicationDate} pk={post.pk} title={post.title} authorpk={post.author} author={user} text={post.text} />)
    switch (x) {
        case null:
            var postis =  False
            break;
    
        default: 
            var postis = True
            break;
    }

    return (
        <div className="content">
            {postis} ? <p>you have no drafts</p> : ""}
            {user ? elements : <p>you have to login first</p>}
        </div>
    )
}
