import { useEffect, useState } from "react"
import Post from "./post"
import axios from "axios"
export default function Drafts() {
    const [posts, setPosts] = useState([])
    const [drafts, setDrafts] = useState([])
    const [author, setAuthor] = useState("")


    function getPosts() {
        axios({
            method: "GET",
            url: "/api/drafts/"
        }).then((response) => {
            const newposts = response.data
            setPosts(newposts)
            console.log(newposts)
        })
    }
    function getAuthor(pk) {
        axios({
            method: "GET",
            url: "/api/users/".concat(pk)
        }).then((response) => {
            setAuthor(response.data.username)
        })
        return author
    }
    useEffect(getPosts, [])
    useEffect(getAuthor,[posts])

    // const x = posts.filter(post => post.publicationDate === null)
    const elements = posts.map(post => <Post pb={post.publicationDate} pk={post.pk} title={post.title} authorpk={post.author} author={getAuthor(post.author)} text={post.text} />)
   

    return (
        <div className="content">
            {elements}
        </div>
    )
}
