import { useState, useEffect } from "react"
import Post from "./post"
import axios from "axios"
export default function Saved() {
    const [posts, setPosts] = useState([])
    const [user, setUser] = useState({})
    var userid = localStorage.getItem("id")
    function getUser() {
        axios.get(`/api/users/${userid}`).then(response => {
            setUser(response.data)
            getPosts(response.data.postsSaved)
        }
        )
    }
    console.log(user)
    useEffect(getUser, [])
    // useEffect(getPosts, [user])
    function getPosts(saved) {
        console.log(saved)
        for (var s = 0; s < saved.length; s++) {
            axios.get(`/api/posts/${saved[s]}`).then(response => setPosts(prev => {
                if (prev.includes(response.data) === false) {
                    { return [...prev, response.data] }
                }
                else{
                    { return [...prev] }

                }
            }))

        }
    }
    console.log(posts)
    const elements = posts.map(post => <Post likes={post.likes} profile={post.profile} publicationDate={post.publicationDate} pb={post.publicationShortDate} pk={post.pk} title={post.title} authorpk={post.author} text={post.text} />)

    return (

        <main>
            {elements}
        </main>
    )


}
