import { Outlet, Link } from "react-router-dom";
import { useState } from "react"; 
import axios from "axios"
export default function Post(props) {
    const page = "/posts/".concat(props.pk)
    const profile = "/users/".concat(props.authorpk)
    const [author,setAuthor] = useState("")
    function getAuthor(pk) {
        axios({
            method: "GET",
            url: "/api/users/".concat(pk)
        }).then((response) => {
            setAuthor(response.data.username)
        })
        return author
    }
    return (
        <div className="post_box">
            <h2><Link className="post_link" to={profile} state={{"pk":props.authorpk}}  >{props.author}</Link></h2>
            <h3><Link className="post_link" to={page} state={props}>{props.title}</Link></h3>
            <p>{props.pb}</p>
            <Outlet/>
        </div>
        
    )
}
