import { Outlet, Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import "./pages.css"
import axios from "axios"

export default function Base() {
    const nav = useNavigate()
    const loggedin = localStorage.getItem("loggedin")
    const id = localStorage.getItem("id")

    function Logout(){
        axios.post("api/logout/")
        localStorage.removeItem("loggedin")
        nav("/")
    }

    return (
        <main>
            <div className="navbar">
                <Link className="link" to="/" > Blog</Link>
                <Link className="link" to="/drafts" >Drafts</Link>
                <Link className="link" to="/create" >Create</Link>
                <Link className="link" to="/topics" >Topics</Link>
                <div className="right_nav">
                    {loggedin ? <button onClick={Logout} id="btn" className="link" >Logout</button> :<Link className="link" to="/signup" >Signup</Link> }
                    {loggedin ?  <Link className="link" to={`/profile/${id}`} >Profile</Link> : <Link className="link" to="/login" >Login</Link> }
                </div>

            </div>
            <Outlet />
        </main>
    )
}
