import { Outlet, Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import "./pages.css"
import axios from "axios"
import { useState } from "react";

export default function Base() {
    const nav = useNavigate()
    const loggedin = localStorage.getItem("loggedin")
    const id = localStorage.getItem("id")
    const profilepk = localStorage.getItem("profile")

    function Logout() {
        axios.get(`/api/logout/${id}/`).then(response => console.log(response.data)).catch(err => console.log(err))
        localStorage.removeItem("loggedin")
        localStorage.removeItem("id")
        localStorage.removeItem("username")
        localStorage.removeItem("email")
        nav("/")
    }

    function getProfile() {
        axios.get(`api/profile/${profilepk}`).then(response =>
            localStorage.setItem('profileimg', response.data.profile_img)
        )
    }

    const profile = localStorage.getItem("profileimg")


    return (
        <main>
            <div className="navbar">
                <Link className="link" to="/" > <h1>Blog</h1></Link>
                <Link className="link" to="/drafts" ><p>Drafts</p></Link>
                <Link className="link" to="/create" ><p>Create</p></Link>
                <Link className="link" to="/topics" ><p>Topics</p></Link>
                <div className="right_nav">
                    {loggedin ? <input type={"button"} onClick={Logout} id="btn" className="link" value={"Logout"} /> : <Link className="link" to="/signup" >Signup</Link>}
                    {loggedin ? <Link className="link" to={`/profile/${id}`} >
                        <div id="navprofile" >
                            <img src={profile} alt="" />
                        </div>
                    </Link> : <Link className="link" to="/login" >Login</Link>}
                </div>

            </div>
            <Outlet />
        </main>
    )
}
