import { useLocation } from "react-router-dom"
import { useState, useEffect } from "react"
import Post from "./post"
import axios from "axios"
export default function Profile(){
    const location = useLocation()
    const user = {username :localStorage.getItem("username") , email:localStorage.getItem("email"), id :localStorage.getItem("id")}
    const [posts, setPosts] = useState([])
    function getPosts() {
        axios(
            {
                method: "get",
                url: "/api/posts/"
            }
        ).then(response => setPosts(response.data))

        axios.get(`/api/profile/${user.id}`).then(response => console.log(response.data))
    }
    const x = posts.filter(post => post.author == user.id)
    const elements = x.map(post => <Post pb={post.publicationShortDate} pk={post.pk} title={post.title} authorpk={post.author} author={user.username} text={post.text} />)
    useEffect(getPosts, [user.id])

    return (
        <div className="profile">
            <div class="profile_image">
                
            </div>
            <h1>{user.username}</h1>
            <h2>{user.email}</h2>
            <div className="content">
                {elements}
            </div>
        </div>
    )
}
