import axios from "axios"
import { useState, useEffect } from "react"
import Post from "./post"

export default function Home() {
    const [posts, setPosts] = useState([])
    const [users,setUsers] = useState([])
    const [author,setAuthor] = useState("") 
    function getPosts() {
        axios({
            method: "GET",
            url: "/api/posts/?format=json"
        }).then((response) => {
            const newposts = response.data
            setPosts(newposts)
            console.log(newposts)
        })
    }
    function getUsers() {
        axios({
            method: "GET",
            url: "/api/users/?format=json"
        }).then((response) => {
            const newusers = response.data
            setUsers(newusers)
            console.log(newusers)
        })
    }
    function getAuthor(url) {
        axios({
            method: "GET",
            url: url.split("8000")[1]
        }).then((response) => {
            setAuthor(response.data.username)
        })
        return author
    }
    useEffect(getPosts, [])
    useEffect(getUsers,[])

    const elements = posts.map(post => <Post title={post.title} author={getAuthor(post.author)} text={post.text} />)

    return (
        <div className="content">
            {elements}
        </div>
    )
}
